function checkPoint() {
  var text;
  var numberGameList = document.getElementById("myInput").value;
  i = numberGameList - 1;
  if (Number(numberGameList) == Number(numberGameList)) {
    text = gameList[i];
  } else {
    text = "Введите корректное значение";
  };
  document.getElementById("main").innerHTML = text;
}
